import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
function Square(props) {
  return (
    <button className="square" onClick={props.onClick}>
      {props.value}
    </button>
  );
}


class Board extends React.Component {


  renderSquare(i) {
    return (
      <Square
        value={this.props.squares[i]}
        onClick={() => this.props.onClick(i)}
      />
    );
  }


  render() {

		let squares = [];
		for(let i = 0; i < 3; i++){
			let row = [];
			for(let j = 0; j < 3; j++) {
				row.push(this.renderSquare(i*3 + j));
			}
			squares.push(<div className="board-row"> {row} </div>);
		}
		return (
			<div>
				{squares}
			</div>
		);
  }
}

class Game extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			history: [{
				squares: Array(9).fill(null),
				x : null,
				y : null,
			}],
			stepNumber: 0,
			xIsNext: true,
			yList : [1,1,1,2,2,2,3,3,3],
			xList : [1,2,3,1,2,3,1,2,3],
		};
	}
	handleClick(i) {
		const history = this.state.history.slice(0,this.state.stepNumber + 1);
		const current = history[history.length - 1];
		const squares = current.squares.slice();
		if(calculateWinner(squares) || squares[i]) {
			return;
		}
    squares[i] = this.state.xIsNext ? 'X' : 'O';
    this.setState({
			history : history.concat([{
				squares: squares,
				x : this.state.xList[i],
				y : this.state.yList[i]
			}]),
			stepNumber : history.length,
      xIsNext: !this.state.xIsNext,
			isAscending : true,
    });
  }
	jumpTo(step) {
		this.setState({
			stepNumber: step,
			xIsNext: (step % 2) === 0
		});
	}
  render() {
		const history  = this.state.history;
		const current = history[this.state.stepNumber];
		const winner = calculateWinner(current.squares);
		const moves = [];
		if(!this.state.isAscending){
		for(let move = history.length-1; move > -1;  move--){
			const step = history[move];
			const desc = move ? 'Go to move #' + move : 'Go to game start';
			const location = step.x ? step.x + ',' + step.y : null;
			if(move === this.state.stepNumber) {
				moves.push(
					<li key={move}>
					<button onClick = { () => this.jumpTo(move)}> <b> {desc} </b> </button>
					<p> {location} </p>
					</li>
				);
			}
			else {
				moves.push(
				<li key={move}>
				<button onClick = { () => this.jumpTo(move)}> {desc} </button> 
				<p> {location} </p>
				</li>
			);
		}
	}
		}
		else{
		for(let move = 0; move < history.length;  move--){
			const step = history[move];
			const desc = move ? 'Go to move #' + move : 'Go to game start';
			const location = step.x ? step.x + ',' + step.y : null;
			if(move === this.state.stepNumber) {
				moves.push(
					<li key={move}>
					<button onClick = { () => this.jumpTo(move)}> <b> {desc} </b> </button>
					<p> {location} </p>
					</li>
				);
			}
			else {
				moves.push(
				<li key={move}>
				<button onClick = { () => this.jumpTo(move)}> {desc} </button> 
				<p> {location} </p>
				</li>
				);
			}
		}
		}


		let toggle = this.state.isAscending ? 'Descending order' : 'Ascending order';
		let status;
		if (winner) {
			status = 'Winner: ' + winner;
		}
		else {
			status =  'Next Player: ' + (this.state.xIsNext ? 'X'  : 'O');
		}
    return (
      <div className="game">
        <div className="game-board">
          <Board 
						squares={current.squares}
						onClick={ (i) => this.handleClick(i) }
				/>
        </div>
        <div className="game-info">
          <div>{status}</div>
					<button>{toggle} </button>
					<ol> {moves} </ol>
        </div>
      </div>
    );
  }
}

// ========================================

ReactDOM.render(
  <Game />,
  document.getElementById('root')
);

function calculateWinner(squares){
	const lines = [
		[0,1,2], 
		[3,4,5],
		[6,7,8],
		[0,3,6],
		[1,4,7],
		[2,5,8],
		[0,4,8],
		[2,4,6],
	];
	for(let i = 0; i < lines.length; i++){
		const  [a, b, c] =  lines[i];
		if(squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
			return squares[a];
		}
	}
	return null;
}
